package main

import (
	"fmt"
	"math/rand"
	"time"
)

func main() {
	rand.Seed(time.Now().UnixNano())
	printPalmTree()
}

func printPalmTree() {
	const trunkHeight = 5
	const leafHeight = 10
	const maxLeafWidth = 15

	fmt.Print("\033[32m") // Green color

	// Print leaves
	for i := 0; i < leafHeight; i++ {
		width := maxLeafWidth - rand.Intn(maxLeafWidth/2)
		for j := 0; j < width; j++ {
			if rand.Intn(2) == 0 {
				fmt.Print(" ")
			} else {
				fmt.Print("^")
			}
		}
		fmt.Println()
	}

	// Print trunk
	fmt.Print("\033[33m") // Brown color
	for i := 0; i < trunkHeight; i++ {
		for j := 0; j < trunkHeight/2; j++ {
			fmt.Print(" ")
		}
		fmt.Println("|||")
	}

	fmt.Print("\033[0m") // Reset color
}
