package main

import (
	"fmt"
	"math/rand"
	"time"
)

var (
	trunkColor = "\033[33m" // Yellow
	leafColor  = "\033[32m" // Green
	resetColor = "\033[0m"
)

func main() {
	rand.Seed(time.Now().UnixNano())
	height := rand.Intn(8) + 12

	for i := 0; i < height-3; i++ {
		fmt.Printf("%s%*s%s\n", trunkColor, height, "|", resetColor)
	}

	for i := height - 3; i < height; i++ {
		fmt.Printf("%s%*s%s\n", leafColor, i*2+1, "/\\", resetColor)
	}
}
